import React, { createContext, useState } from 'react';

export const CartContext = createContext(); // Create a new context

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]); // State to hold cart items

  const addToCart = (item) => {
    setCartItems([...cartItems, item]); // Add item to cart
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart }}>
      {children}
    </CartContext.Provider>
  );
};

export default { CartContext, CartProvider }; // Export an object literal containing both CartContext and CartProvider
