import React from 'react';
import { Link } from 'react-router-dom';

const MenuItem = ({ itemName, itemType, itemId }) => {
  return (
    <div>
      <p>
        <Link to={`/${itemType}/${itemId}`}>{itemName}</Link>
      </p>
    </div>
  );
};

export default MenuItem;
