import React, { useState, useEffect } from 'react';
import SnackOrBoozeApi from './api';
import MenuItem from './MenuItem';
import Menu from './Menu';

const SnackPage = () => {
  const [snackItems, setSnackItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]); // State for the shopping cart

  useEffect(() => {
    async function fetchSnacks() {
      try {
        const snacks = await SnackOrBoozeApi.getSnacks();
        setSnackItems(snacks);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching snacks:', error);
        setIsLoading(false);
      }
    }

    fetchSnacks();
  }, []);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]); // Add item to the shopping cart
  };

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Menu items={snackItems.map((snack) => snack.name)} itemName="Snack" />
      {snackItems.map((snack) => (
        <MenuItem
          itemType="snacks"
          key={snack.id}
          itemName={snack.name}
          itemQuantity={snack.quantity}
          itemId={snack.id}
          addToCart={addToCart} // Pass the addToCart function
        />
      ))}
      <Cart items={cartItems} /> {/* Render the Cart component */}
    </div>
  );
};

export default SnackPage;
