import React, { useState, useEffect } from 'react';
import SnackOrBoozeApi from './api';
import Menu from './Menu';
import MenuItem from './MenuItem';

const DrinkPage = () => {
  const [drinkItems, setDrinkItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]); // State for the shopping cart

  useEffect(() => {
    async function fetchDrinks() {
      try {
        const drinks = await SnackOrBoozeApi.getDrinks();
        setDrinkItems(drinks);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching drinks:', error);
        setIsLoading(false);
      }
    }

    fetchDrinks();
  }, []);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]); // Add item to the shopping cart
  };

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Menu items={drinkItems.map((drink) => drink.name)} itemName="Drink" />
      {drinkItems.map((drink) => (
        <MenuItem
          itemType="drinks"
          key={drink.id}
          itemName={drink.name}
          itemQuantity={drink.quantity}
          itemId={drink.id}
          addToCart={addToCart} // Pass the addToCart function
        />
      ))}
      <Cart items={cartItems} /> {/* Render the Cart component */}
    </div>
  );
};

export default DrinkPage;
