// export default MenuItem;
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext'; // Import useCart instead of CartProvider

const MenuItem = ({ itemName, itemType, itemId, itemQuantity }) => {
  const { addToCart } = useCart(); // Use useCart hook to access the cart context

  const handleAddToCart = () => {
    // Define a function to handle adding the item to the cart
    addToCart({
      id: itemId,
      name: itemName,
      type: itemType,
      quantity: itemQuantity
    });
  };

  return (
    <div>
      <p>
        <Link to={`/${itemType}/${itemId}`}>{itemName}</Link>
      </p>
      <p>Quantity: {itemQuantity} </p>
      <p>url: {itemType}/{itemId}</p>
      <button onClick={handleAddToCart}>Add to Cart</button> {/* Call handleAddToCart onClick */}
    </div>
  );
};

export default MenuItem;
