import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import SnackOrBoozeApi from './api';

const ItemDetail = () => {
  const { itemType, id } = useParams(); // Use useParams to get the id parameter from the URL
  console.log('itemType:', itemType); // Log the value of itemType    ITEM TYPE is alsways undefined 
  const [item, setItem] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function fetchItem() {
      try {
        const items = await (itemType === 'snacks'
          ? SnackOrBoozeApi.getSnacks()
          : SnackOrBoozeApi.getDrinks());

        console.log('Fetched items:', items);

        const selectedItem = items.find((item) => item.id === id);

        console.log('Selected item:', selectedItem);

        setItem(selectedItem);
        setIsLoading(false);
      } catch (error) {
        console.error(`Error fetching ${itemType}:`, error);
        setIsLoading(false);
      }
    }

    fetchItem();
  }, [id, itemType]);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (!item) {
    return <p>Item not found</p>;
  }

  return (
    <div>
      <h2>{item.name}</h2>
      <p>Description: {item.description}</p>
      <p>Recipe: {item.recipe}</p>
    </div>
  );
};

export default ItemDetail;
