import React from 'react';
import { Link } from 'react-router-dom';
import SnackOrBoozeApi from './api'; // Import your API
import './MenuItem.css'; // Import CSS file

const MenuItem = ({ itemName, itemType, itemId, itemQuantity }) => {
  const handlePurchase = async () => {
    try {
      console.log('Sending POST request to update item quantity (Purchase)');
      await SnackOrBoozeApi.updateItemQuantity(itemType, itemId, -1); // Subtract 1 from quantity
      // Update the UI or perform any necessary actions after successful purchase
      console.log(`${itemName} purchased successfully.`);
      // Reload the page to reflect the updated quantity
      window.location.reload();
    } catch (error) {
      console.error('Error purchasing item:', error);
    }
  };

  const handleReturn = async () => {
    try {
      console.log('Sending POST request to update item quantity (Return)');
      await SnackOrBoozeApi.updateItemQuantity(itemType, itemId, 1); // Add 1 to quantity
      // Update the UI or perform any necessary actions after successful return
      console.log(`${itemName} returned successfully.`);
      // Reload the page to reflect the updated quantity
      window.location.reload();
    } catch (error) {
      console.error('Error returning item:', error);
    }
  };

  return (
    <div className="menu-item-container">
      <p className="menu-item-info">
        <Link to={`/${itemType}/${itemId}`} className="menu-item-link">{itemName}</Link>
      </p>
      <p className="menu-item-info">Quantity: <span className="menu-item-quantity">{itemQuantity}</span></p>
      <div>
        <button className="menu-item-button" onClick={handlePurchase}>Purchase</button>
        <button className="menu-item-button" onClick={handleReturn}>Return</button>
      </div>
    </div>
  );
};

export default MenuItem;
