import React from 'react';
import { Link } from 'react-router-dom';

const MenuItem = ({ itemName, itemType, itemId, itemQuantity, addToCart }) => {
  const handleAddToCart = () => {
    addToCart({ id: itemId, name: itemName, quantity: 1 }); // Add the item to the cart
  };

  return (
    <div>
      <p>
        <Link to={`/${itemType}/${itemId}`}>{itemName}</Link>
      </p>
      <p>Quantity: {itemQuantity} </p>
      <p>url: {itemType}/{itemId}</p>
      <button onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
};

export default MenuItem;
