import React, { useState, useEffect } from 'react';
import SnackOrBoozeApi from './api';

const ItemDetail = ({ match, itemType }) => {
  const [item, setItem] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const { id } = match.params;

    async function fetchItemDetails() {
      try {
        const itemDetails = await (itemType === 'snacks'
          ? SnackOrBoozeApi.getSnack(id)
          : SnackOrBoozeApi.getDrink(id));

        setItem(itemDetails);
        setIsLoading(false);
      } catch (error) {
        console.error(`Error fetching ${itemType} details:`, error);
        setIsLoading(false);
      }
    }

    fetchItemDetails();
  }, [match.params, itemType]);

  if (isLoading) {
    return <p>Loading {itemType} details...</p>;
  }

  return (
    <div>
      <h2>{item.name}</h2>
      <p>Description: {item.description}</p>
      <p>Recipe: {item.recipe}</p>
    </div>
  );
};

export default ItemDetail;
