import React, { useState, useEffect } from 'react';
import SnackOrBoozeApi from './api';
import MenuItem from './MenuItem';
import Menu from './Menu';

const SnackPage = () => {
  const [snackItems, setSnackItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    async function fetchSnacks() {
      try {
        const snacks = await SnackOrBoozeApi.getSnacks();
        setSnackItems(snacks);
      } catch (error) {
        console.error('Error fetching snacks:', error);
      }
    }

    fetchSnacks();
  }, []);

  // Function to add item to the cart
  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  return (
    <div>
      <Menu items={snackItems.map((snack) => snack.name)} itemName="Snack" />
      {snackItems.map((snack) => (
        <MenuItem
          key={snack.id}
          itemName={snack.name}
          itemQuantity={snack.quantity}
          itemId={snack.id}
          onAddToCart={() => addToCart(snack)} // Pass the addToCart function as a prop
        />
      ))}
    </div>
  );
};

export default SnackPage;
