import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from './CartContext'; // Assuming you have a CartContext defined

const MenuItem = ({ itemName, itemType, itemId, itemQuantity }) => {
  const { addToCart } = useContext(CartContext); // Access the addToCart function from the CartContext

  const handleAddToCart = () => {
    addToCart({ itemName, itemType, itemId }); // Call addToCart with the item details
  };

  return (
    <div>
      <p>
        <Link to={`/${itemType}/${itemId}`}>{itemName}</Link>
      </p>
      <p>Quantity: {itemQuantity} </p>
      <p>url: {itemType}/{itemId}</p>
      <button onClick={handleAddToCart}>Add to Cart</button> {/* Attach onClick handler */}
    </div>
  );
};

export default MenuItem;
